// The Vue build version to load with the `import` command
// (runtime-only or standalone) has been set in webpack.base.conf with an alias.
import Vue from 'vue'
import App from './App'
import router from './router'

//引入jQuery
import $ from 'jquery'

//引入bootstrap
import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap/dist/js/bootstrap.min'

//引入弹出框插件
import './assets/jqueryConfirm/js/jquery-confirm'
import './assets/jqueryConfirm/js/jquery.sticky.min'
import './assets/jqueryConfirm/css/jquery-confirm.css'

//引入vuex
import Vuex from 'vuex'
Vue.use(Vuex);

//引入cookies处理插件
import VueCookies from 'vue-cookies';
Vue.use(VueCookies);

Vue.config.productionTip = false;

router.beforeEach((to, from, next) => {
    //to即将进入的目标路由对象，from当前导航正要离开的路由， next : 下一步执行的函数钩子
    if (to.path === '/login') { next() } // 如果即将进入登录路由，则直接放行
    else {   //进入的不是登录路由
        if (to.meta.requiresAuth && !VueCookies.get('accessToken')) { 
            next({ path: '/login' });
        }
        //下一跳路由需要登录验证，并且还未登录，则路由定向到 登录路由
        else { 
            next();
        }
    }
    //如果不需要登录验证，或者已经登录成功，则直接放行
})

const store = new Vuex.Store({
    state: {
      ajaxUrl: 'http://natapp.rayhahah.com/user/'
    },
    mutations: {
        
    }
  })

/* eslint-disable no-new */
new Vue({
    el: '#app',
    router,
    store,
    components: { App },
    template: '<App/>'
})
