import Vue from 'vue'
import Router from 'vue-router'
import login from '@/components/Login'
import home from '@/components/Home'
import user from '@/components/User'
import picture from '@/components/Picture'

Vue.use(Router)

export default new Router({
    routes: [
        {
            path: '/',
            name: 'login',
            component: login
        },
        {
            path: '/home',
            name: 'home',
            component: home,
            meta: {
                requiresAuth: true
            },
            children: [
                {
                    path: '',
                    component: user
                },
                {
                    path: '/user',
                    component: user
                },
                {
                    path: '/picture',
                    component: picture
                }
            ]
        }
    ]
})