import Vue from 'vue'
import Router from 'vue-router'
import login from '@/components/Login'
import home from '@/components/Home'
import user from '@/components/User'
import picture from '@/components/Picture'

Vue.use(Router)

export default new Router({
    routes: [
        {
            path: '/',
            redirect:'/picture'
        },
        {
            path:'/login',
            name: 'login',
            component: login
        },
        {
            path: '/home',
            name: 'home',
            component: home,
            meta: {
                requiresAuth: true
            },
            children: [
                {
                    path: '',
                    component: picture,
                    meta: {
                        requiresAuth: true
                    },
                },
                {
                    path: '/user',
                    component: user,
                    meta: {
                        requiresAuth: true
                    },
                },
                {
                    path: '/picture',
                    component: picture,
                    meta: {
                        requiresAuth: true
                    },
                }
            ]
        }
    ]
})